arguments.argument:
x = view_xview[0];
y = view_yview[0]+view_hview[0]-sprite_height;

if global.typing
{
    draw_sprite(ConsoleBackgroundS, 0, x, y)
}

draw_sprite(sprite_index,0,x,y);

draw_set_color(make_color_rgb(0, 255, 255));// Find best Color. Orange just sucks.

draw_set_color(c_white);
draw_set_valign(fa_top);
draw_set_halign(fa_left);
draw_set_alpha(1);

var visible_string_width;
visible_string = string_copy(text,0,seek);
visible_string_width = string_width(visible_string);

/*if (visible_string_width>sprite_width){
    draw_set_halign(fa_right);
    //draw_text(x+sprite_width-(string_width("_")*0.7),y,text);
    draw_text(x+sprite_width-1,y,visible_string);
    draw_set_halign(fa_left);
}else{*/
draw_text(x,y,text);
//}

if (draw_cursor) {
    var cx, cy, temp;
    temp = text + " ";
    /*if (visible_string_width>sprite_width){
        //cx = x+sprite_width-(string_width("_")*0.7);
        cx = x+sprite_width;
    }else{*/
    cx = x+visible_string_width;
    //}
    cy = y+string_height(text+" ")-string_height("_");
    draw_line(cx,cy,cx,cy+string_height("_"));
    //draw_text_transformed(cx,cy,"_",string_width(temp[seek])/string_width("_"),1,0);
}

var listlen, listheight;
listlen = ds_list_size(global.ConsoleLog);
listheight = string_height("_")*listlen;
var i;
draw_set_color(make_color_rgb(0, 255, 255));
for (i = 0; i < listlen; i += 1) {

    if string_copy(ds_list_find_value(global.ConsoleLog, i), 0, 2) = "/r"
    {
        draw_set_color(c_red)
        draw_text(x,y-listheight+string_height("_")*i,string_copy(ds_list_find_value(global.ConsoleLog,i), 3, string_length(ds_list_find_value(global.ConsoleLog,i))));
        draw_set_color(make_color_rgb(0, 255, 255));
    }
    else if string_copy(ds_list_find_value(global.ConsoleLog, i), 0, 2) = "/g"
    {
        draw_set_color(c_green)
        draw_text(x,y-listheight+string_height("_")*i,string_copy(ds_list_find_value(global.ConsoleLog,i), 3, string_length(ds_list_find_value(global.ConsoleLog,i))));
        draw_set_color(make_color_rgb(0, 255, 255));
    }
    else if string_copy(ds_list_find_value(global.ConsoleLog, i), 0, 2) = "/b"
    {
        draw_set_color(make_color_rgb(0, 130, 255));
        draw_text(x,y-listheight+string_height("_")*i,string_copy(ds_list_find_value(global.ConsoleLog,i), 3, string_length(ds_list_find_value(global.ConsoleLog,i))));
        draw_set_color(make_color_rgb(0, 255, 255));
    }
    else if string_copy(ds_list_find_value(global.ConsoleLog, i), 0, 2) = "/v"
    {
        draw_set_color(c_purple)
        draw_text(x,y-listheight+string_height("_")*i,string_copy(ds_list_find_value(global.ConsoleLog,i), 3, string_length(ds_list_find_value(global.ConsoleLog,i))));
        draw_set_color(make_color_rgb(0, 255, 255));
    }
    else if string_copy(ds_list_find_value(global.ConsoleLog, i), 0, 2) = "/w"
    {
        draw_set_color(c_white)
        draw_text(x,y-listheight+string_height("_")*i,string_copy(ds_list_find_value(global.ConsoleLog,i), 3, string_length(ds_list_find_value(global.ConsoleLog,i))));
        draw_set_color(make_color_rgb(0, 255, 255));
    }
    else
    {
        draw_text(x,y-listheight+string_height("_")*i,ds_list_find_value(global.ConsoleLog,i));
    }
}
