arguments.argument:
if global.typing
{
    if mode != "chat"
    {
        print(text);
    }

    processCommand(text)

    while (ds_list_size(global.ConsoleCmdLog)>10) {
        ds_list_delete(global.ConsoleCmdLog,0);
    }

    text = "";
    keyboard_string = "_";
    seek = 0;

    global.typing = 0
}
else
{
    global.typing = 1
}
