arguments.argument:
if lastNode != -1 and global.botLearningMode
{
    if ds_list_find_index(lastNode.connections, other.id) >= 0
    {
        if distanceTraveled < ds_list_find_value(lastNode.distance, ds_list_find_index(lastNode.connections, other.id))
        {
            // The distance between them is shorter than the bots think
            // Correct the fault
            ds_list_replace(lastNode.distance, ds_list_find_index(lastNode.connections, other.id), distanceTraveled)
            global.changedNodeMap = 1
        }
    }
}

lastNode = other.id
distanceTraveled = 0
