arguments.argument:
if clearScreenTimer > 0// Don't just delete everything, make it zoom upwards.
{
    Console_print('');
    Console_print('');
    Console_print('');
    Console_print('');
    Console_print('');
    clearScreenTimer -= 5;
}

// Checks for any input
if keyboard_string != ""
{
    current_text = string_copy(current_text, 0, cursor_position) + keyboard_string + string_copy(current_text, cursor_position+1, string_length(current_text));
    cursor_position += string_length(keyboard_string);
    keyboard_string = "";
}

if keyboard_check_pressed(vk_up)
{
    commandLogIndex = max(0, commandLogIndex-1);
    keyboard_string = ds_list_find_value(global.commandLog, commandLogIndex);
}

if keyboard_check_pressed(vk_down)
{
    commandLogIndex += 1;
    if commandLogIndex >= ds_list_size(global.commandLog)
    {
        commandLogIndex = ds_list_size(global.commandLog);
        keyboard_string = "";
    }
    else
    {
        keyboard_string = ds_list_find_value(global.commandLog, commandLogIndex);
    }
}
