arguments.argument:
var a;

with Node
{
    if id == other.id continue;// Ignore yourself

    a = ds_list_find_index(connections, other.id)
    if a >= 0
    {
        ds_list_delete(connections, a)
        ds_list_delete(distance, a)
        ds_list_delete(commands, a)
        ds_list_delete(network, ds_list_find_index(network, other.id))
    }
}
