value[0] = global.bot_num_wished;

if global.bot_mode == 1
{
    value[1] = "Fix"
}
else if global.bot_mode == 2
{
    value[1] = "Dynamic"
}
else
{
    value[1] = "Off"
}

if global.bot_coop
{
    value[2] = "On"
}
else
{
    value[2] = "Off"
}

