arguments.argument:
global.bot_mode = global.bot_mode+1
if global.bot_mode == 3
{
    global.bot_mode = 0
}

ini_open("gg2.ini")
ini_write_real("Bots", "Mode", global.bot_mode)
ini_close()

if global.bot_mode == 1
{
    value[1] = "Fix"
}
else if global.bot_mode == 2
{
    value[1] = "Dynamic"
}
else
{
    value[1] = "Off"
}
