arguments.argument:
global.recordingEnabled = !global.recordingEnabled

ini_open("gg2.ini");
ini_write_real("Server", "Recording Enabled", global.recordingEnabled);
ini_close();

if(global.recordingEnabled) {
    value[9] = "Enabled";
    global.justEnabledRecording = 1;
} else {
    value[9] = "Disabled";
    global.justEnabledRecording = 0;
}
