arguments.argument:
global.consoleOpen = !global.consoleOpen

switch (global.consoleOpen)
{
    case 0:
        item[2] = "Open Console";
        with Console
        {
            instance_destroy();
        }
        break;

    case 1:
        item[2] = "Close Console";
        instance_create(x, y, Console)
        break;
}
