arguments.argument:
var i;
draw_set_color(c_black);
draw_set_alpha(0.8);

draw_rectangle(xbegin-10, ybegin-10, xbegin+width+25, 550, false);

draw_set_alpha(1);
draw_set_color(c_white);

draw_set_valign(fa_top);

draw_text(xbegin, ybegin-2*spacing, "Plugins ("+string(numPlugins)+") ");

var plugin;
for(i=offset; i<ds_list_size(plugins) and i<offset+items; i+=1) {
    plugin = ds_list_find_value(plugins, i);

    if(i==virtualitem) {
        draw_set_color(c_red);
    }else{
        draw_set_color(c_white);
    }

    draw_set_halign(fa_left);
    draw_text(xbegin+10, ybegin+spacing*(i-offset),"["+plugin.downloads+"]");
    draw_text(xbegin+95, ybegin+spacing*(i-offset),plugin.name);

    if (plugin.installed) {
        if (plugin.enabled) {
            draw_set_color(c_green);
            draw_text(xbegin+95, ybegin+spacing*(i-offset),"#"+"Enabled");
        }else{
            draw_set_color(c_red);
            draw_text(xbegin+95, ybegin+spacing*(i-offset),"#"+"Disabled");
        }
    }else{
        draw_set_color(c_gray);
        draw_text(xbegin+95, ybegin+spacing*(i-offset),"#"+"Not Installed");
    }
    draw_set_color(c_white);

    draw_set_halign(fa_right);
    draw_text(xbegin+425, ybegin+spacing*(i-offset),plugin.author);
}
